    def interactive_session(self):
        """Start interactive chat session"""
        
        self.console.print(Panel(
            "[bold green]Welcome to Buddy AI![/bold green]\n"
            "Type your requests and I'll help you with various tasks.\n"
            "Commands: /quit to exit, /clear to clear history",
            title="ü§ñ Buddy AI Assistant",
            border_style="green"
        ))
        
        while True:
            try:
                # Multi-line input handling
                self.console.print("\nüí¨ You: ", end="")
                lines = []
                while True:
                    try:
                        line = input()
                        if line.strip() == "":  # Empty line ends input
                            break
                        lines.append(line)
                    except EOFError:  # Ctrl+D ends input
                        break
                
                user_input = "\n".join(lines).strip()
                
                if not user_input:
                    continue
                
                # Handle commands
                if user_input.lower() in ['/quit', '/exit', 'quit', 'exit']:
                    self.console.print("[yellow]üëã Goodbye![/yellow]")
                    break
                elif user_input.lower() in ['/clear', 'clear']:
                    self.console.clear()
                    continue
                
                # Process the request
                result = self.process_request(user_input)
                
                if not result.get("success", False):
                    self.console.print(f"[red]‚ùå Error: {result.get('error', 'Unknown error')}[/red]")
                
            except KeyboardInterrupt:
                self.console.print("\n[yellow]üëã Goodbye![/yellow]")
                break
            except Exception as e:
                self.console.print(f"[red]‚ùå Session error: {e}[/red]")


